$(document).ready(function(){function e(e){$("#ajax-message").html(e),$("#ajax-message").fadeIn(1e3)}$("#contact_form").submit(function(a){a.preventDefault(),$("#ajax-message").hide(),$.ajax({url:"/",type:"post",dataType:"html",data:$(this).serialize(),error:function(a,t,s){e("Sorry, there was an error submitting your form. Please call me at 999-999-9999 and I'll be glad to assist you.")},success:function(a,t,s){if(a.match(/<title>Error<\/title>/)){var r=$(a).find("ul li:first").text();"A valid sender email is required"==r?e("Please enter your email address."):"Email Message is Required"==r&&e("Please enter a message.")}else e("Thanks for contacting me!")}})})});